package oasis

import (
	"testing"

	"github.com/ethereum/go-ethereum/common/hexutil"
)

var err error
var client *Client

func init() {
	client, err = NewClient("https://testnet.sapphire.oasis.io", Local, &Option{
		LocalAddress:   "0x7ccCc552F55C05FD33d9827070E1dB3D28322622",
		TestnetAddress: "0x99e514Dc90f4Dd36850C893bec2AdC9521caF8BB",
	})
	if err != nil {
		panic(err)
	}
}

func TestClient_PublicKey(t *testing.T) {
	/* alpha
	   oasis_test.go:36: 0x0363f549d250342df02ee8b51ad6c9148dabc587c6569761ab58aa68488bd2e2c5
	   oasis_test.go:36: 0x031cbb294f9955d80f65d9499feaeb5cb29d44c070adddd75cd48a40791d39b971
	   oasis_test.go:36: 0x035c54e8287a7f7ba31886249fc89f295a4cb74cebf0d925f1eafe87f22fba57f9
	*/

	/*  beta1
	    oasis_test.go:27: 0x034def276d763bfb937a4f356d26b58cb0428bc198d000b91630db5d04bb7f35dc
		oasis_test.go:27: 0x03183ee062dafa5a0e536ba497c6375a996364682bf22cd5de989df1b0b9d23621
	    oasis_test.go:27: 0x03a868050ec7b61b6956d6c1ca722f4d2a32671902486980d5fd6ebf9b4c64dd93
	*/
	publicKey, err := client.PublicKey()
	if err != nil {
		t.Fatal(err)
	}
	for _, item := range publicKey {
		t.Logf("%v\n", hexutil.Encode(item))
	}
}

func TestClient_SignBtcTx(t *testing.T) {
	// https://holesky.etherscan.io/tx/0xeaf28c6b4837a7f21db7854fae273a6f33e81b3560539f0e0814072b69a25981

	//    ethereum_test.go:41
	txRaw, err := hexutil.Decode("")
	if err != nil {
		t.Fatal(err)
	}

	//    ethereum_test.go:42
	receiptRaw, err := hexutil.Decode("")
	if err != nil {
		t.Fatal(err)
	}

	proofData, err := hexutil.Decode("")
	if err != nil {
		t.Fatal(err)
	}

	sigs, err := client.SignBtcTx(txRaw, receiptRaw, proofData)
	if err != nil {
		t.Fatal(err)
	}

	t.Logf("%x\n", sigs)
}

func TestClient_AlphaPublicKey(t *testing.T) {
	publicKey, err := client.TestnetPublicKey()
	if err != nil {
		t.Fatal(err)
	}
	for _, pub := range publicKey {
		t.Logf("%x\n", pub)
	}
}

