package sgx

import "testing"

var client *Client
var err error

func init() {
	client = NewClient("http://127.0.0.1:8989")
}
func TestClient_SgxKeyInfo(t *testing.T) {
	keyInfo, err := client.SgxKeyInfo()
	if err != nil {
		t.Fatal(err)
	}
	t.Log(keyInfo)
}

func TestClient_BtcTxSignature(t *testing.T) {
	sig, err := client.BtcTxSignatureV1(
		"2e2c7b0614ac2d2e940cb2b9bf6876a6ce2c4d334ef0c7be88a687b0df2408d2",
		"3956407cdb9489cdcf9329db4c02c69918d920f5f40ce0adde9c321399830724",
		"",
		"80000000000000",
		[]string{"a7f740000b5390f8c05c88d079332a88993ecaef1bea85126cbf9cf1cb9172d4"},
	)
	if err != nil {
		t.Fatal(err)
	}
	for _, value := range sig.Signatures {
		t.Log(value)
	}
}
